CREATE TABLE IF NOT EXISTS "Ressources_Listes" ("ID"	INTEGER PRIMARY KEY AUTOINCREMENT, "RESSOURCES_NOM_FR"	TEXT UNIQUE, "RESSOURCES_ABBREVIATION"	TEXT, "ID_GROUP"	INTEGER, "ID_RARETE"	INTEGER, "RESSOURCES_PRIX"	INTEGER, "RESSOURCES_ICONE"	TEXT, FOREIGN KEY("ID_GROUP") REFERENCES "Ressources_Groupes"("ID"), FOREIGN KEY("ID_RARETE") REFERENCES "Ressources_Raretes"("ID"));
CREATE TABLE IF NOT EXISTS "Recettes" ("ID"	INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE, "RECETTE_NOM_FR"	TEXT, "RECETTE_DESCRIPTION_COURTE"	TEXT, "RECETTE_DESCRIPTION"	TEXT, "RECETTE_NIVEAU"	INTEGER, "RECETTE_COMPOSANT_ID"	INTEGER, "RECETTE_QUANTITEE_COMPOSANT"	INTEGER, "RECETTE_QUANTITEE_OBTENUE"	INTEGER, "RECETTE_ICONE"	TEXT);
CREATE TABLE IF NOT EXISTS "Ressources_Farm" ("ID"	INTEGER PRIMARY KEY AUTOINCREMENT, "ID_RESSOURCE"	INTEGER, "NOM_RESSOURCE"	TEXT, "NOM_PLANTE"	TEXT, "QUANTITEE_PAR_PLANT"	INTEGER);
CREATE TABLE IF NOT EXISTS "Ressources_Groupes" ( "ID"	INTEGER PRIMARY KEY AUTOINCREMENT, "GROUPE_NOM"	TEXT NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "Ressources_Raretes" ( "ID"	INTEGER PRIMARY KEY AUTOINCREMENT, "RARETE_NOM"	TEXT NOT NULL UNIQUE);
CREATE UNIQUE INDEX IF NOT EXISTS "IDX_RESSOURCES_NOM_FR" ON "Ressources_Listes" ("RESSOURCES_NOM_FR"	ASC);
CREATE UNIQUE INDEX IF NOT EXISTS "IDX_RESSOURCES_ID" ON "Ressources_Listes" ("ID"	ASC);
CREATE UNIQUE INDEX IF NOT EXISTS "IDX_GROUPES_ID" ON "Ressources_Groupes" ("ID"	ASC);
CREATE UNIQUE INDEX IF NOT EXISTS "IDX_GROUPES_NOM" ON "Ressources_Groupes" ("GROUPE_NOM"	ASC);
CREATE UNIQUE INDEX IF NOT EXISTS "IDX_RARETE_ID" ON "Ressources_Raretes" ("ID"	ASC);
CREATE UNIQUE INDEX IF NOT EXISTS "IDX_RARETE_NOM" ON "Ressources_Raretes" ("RARETE_NOM"	ASC);
